// React
import React from 'react';
// Components
import { Routes, Route, NavLink } from 'react-router-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
// Styles
import './App.css';
import 'font-awesome/css/font-awesome.min.css';

function App() {
  return (
    <div className="App">
      <>
        <TestNavigationBar />
        <Routes>
          <Route path='/' element={<HomePage />} />
          <Route path='/bathroom' element={<BathroomPage />} />
          <Route path='/reviews' element={<p>Reviews</p>} />
          <Route path='/signin' element={<SignInPage />} />
        </Routes>
      </>
    </div>
  );
}

class HomePage extends React.Component {
  render() {
    return (
      <div>
        <h1>Spotty</h1>
        <h2>Spot-a-Potty</h2>
        <p>home!!!</p>
        <form action="" className="mt-3">
          <input type="text" placeholder="Location" />
          <Button data-testid="searchBtn" variant="primary btn-sm" id="search-button" type="submit">
              <i className="fa fa-search" aria-hidden="true"></i>
          </Button>{' '}
        </form>
      </div>
    );
  }
}

class BathroomPage extends React.Component {
  render() {
    return (
      <div>
        <h1>Building Title</h1>
        <p>Address</p>
        <h2>Gallery</h2>
        <h2>Features</h2>
        <h2>Rating</h2>
      </div>
    );
  }
}

class SignInPage extends React.Component {
  render() {
    return (
      <div>
        <h1>Spotty</h1>
        <h2>(LOGO)</h2>
        <form class="form-inline" onSubmit={this.props.data} action="" className="mt-3">
          <p>ID</p>
          <input type="text" placeholder="Location" />
        </form>

        <form class="form-inline">
          <div class="form-group mr-3">
            <label for="searchQuery" class="mr-2">What do you want to hear?</label>
            <input type="text" name="term" id="searchQuery" class="form-control"/>
          </div>
          <button type="submit" class="btn btn-primary">
            <i class="fa fa-music" aria-hidden="true"></i> Search!
          </button>
          <input type="hidden" name="entity" value="song"/>
        </form>


        <Button data-testid="searchBtn" variant="primary btn-sm" id="search-button" type="submit">
          <i className="fa-solid fa-right-to-bracket" aria-hidden="true"></i>Log In
        </Button>
        {/*<FontAwesomeIcon icon="fa-solid fa-right-to-bracket" />*/}
      </div>
    );
  }
}

class TestNavigationBar extends React.Component {
  render() {
    return (
      <div className="header-div">
          <Navbar expand="lg">
            <Navbar.Brand><NavLink exact to='/' className="navLink">Spotty</NavLink></Navbar.Brand>
            {/*<Navbar.Toggle aria-controls="basic-navbar-nav" />*/}
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                    <Nav.Link><NavLink exact to='/' className="navLink" >Home</NavLink></Nav.Link>
                    <Nav.Link><NavLink exact to='/bathroom' className="navLink" >Bathroom</NavLink></Nav.Link>
                    <Nav.Link><NavLink exact to='/reviews' className="navLink">Review History</NavLink></Nav.Link>
                    <Nav.Link><NavLink exact to='/signin' className="navLink">Sign In</NavLink></Nav.Link>
                </Nav>
            </Navbar.Collapse>
          </Navbar>
      </div>
    )
  }
}

export default App;
