// React
import React from 'react';
// Components
import { Routes, Route, NavLink } from 'react-router-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';
// Styles
import './App.css';
import 'font-awesome/css/font-awesome.min.css';

function App() {
  return (
    <div className="App">
      <>
        <TestNavigationBar />
        <Routes>
          <Route path='/' element={<HomePage />} />
          <Route path='/bathroom' element={<BathroomPage />} />
          <Route path='/reviews' element={<ReviewHistoryPage />} />
          <Route path='/signin' element={<SignInPage />} />
        </Routes>
      </>
    </div>
  );
}

class HomePage extends React.Component {
  render() {
    return (
      <div>
        <h1>Spotty</h1>
        <h2>Spot-a-Potty</h2>
        <form action="" className="mt-3">
          <input type="text" placeholder="Location" />
          <Button data-testid="searchBtn" variant="primary btn-sm" id="search-button" type="submit">
              <i className="fa fa-search" aria-hidden="true"></i>
          </Button>{' '}
        </form>
      </div>
    );
  }
}

class BathroomPage extends React.Component {
  render() {
    return (
      <div>
        <h1>Building Title</h1>
        <p>Address</p>
        <h2>Gallery</h2>
        <h2>Features</h2>
        <h2>Rating</h2>
      </div>
    );
  }
}

class ReviewHistoryPage extends React.Component {
  render() {
    let borderStyle = "5px solid red";

    return (
      <div>
        <h1>Reviews</h1>
        <p>Address</p>
        <h2>Gallery</h2>
        <h2>Features</h2>
        <h2>Rating</h2>
        <ListGroup variant="flush">
          <ListGroup.Item className="session">
            <div className="d-flex w-100 justify-content-between">
                <h3 className="list-favorites mb-1" style={{borderLeft: borderStyle, paddingLeft: "7px", marginTop: "1rem", fontSize: "20px"}}>Title</h3>
                <p className="list-book-count" style={{marginBottom: "1.5rem", marginLeft: ".5rem"}}>5 books</p>
            </div>
            <i className="open-details fa fa-chevron-right" aria-hidden="true"></i>
          </ListGroup.Item>
        </ListGroup>
      </div>
    );
  }
}

class SignInPage extends React.Component {
  render() {
    return (
      <div>
        <h1>Sign In</h1>
        <h1>Spotty</h1>
        <h2>(LOGO)</h2>
        <form class="form-inline">
          <div class="form-group mr-3">
            <label for="searchQuery" class="mr-2">ID </label>
            <input type="text" name="term" id="searchQuery" class="form-control"/>
          </div>
          <div class="form-group mr-3">
            <label for="searchQuery" class="mr-2">Password </label>
            <input type="text" name="term" id="searchQuery" class="form-control"/>
          </div>
          <button type="submit" class="btn btn-primary">
            <i className="fa-solid fa-right-to-bracket" aria-hidden="true"></i>Sign In
          </button>
        </form>
        <p>Forgot your ID or password?</p>
        <button type="submit" class="btn btn-primary">Sign Up</button>
      </div>
    );
  }
}

class TestNavigationBar extends React.Component {
  render() {
    return (
      <div className="header-div">
          <Navbar expand="lg">
            <Navbar.Brand><NavLink exact to='/' className="navLink">Spotty</NavLink></Navbar.Brand>
            {/*<Navbar.Toggle aria-controls="basic-navbar-nav" />*/}
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                    <Nav.Link><NavLink exact to='/' className="navLink" >Home</NavLink></Nav.Link>
                    <Nav.Link><NavLink exact to='/bathroom' className="navLink" >Bathroom</NavLink></Nav.Link>
                    <Nav.Link><NavLink exact to='/reviews' className="navLink">Review History</NavLink></Nav.Link>
                    <Nav.Link><NavLink exact to='/signin' className="navLink">Sign In</NavLink></Nav.Link>
                </Nav>
            </Navbar.Collapse>
          </Navbar>
      </div>
    )
  }
}

export default App;
